if (!defined('ABSPATH')) exit; // Exit if accessed directly

global $product , $post;
?>

<?php
/**
 * woocommerce_before_single_product hook
 *
 * @hooked woocommerce_show_messages - 10
 */
do_action('woocommerce_before_single_product');
?>
<div id="product-<?php the_ID(); ?>" <?php wc_product_class('', $product); ?>>
    <div class="product_detail row">
        <div class="col-lg-4 col-md-4 col-sm-12 col-xs-12 clear_xs">
            <div class="slider_img_productd">
                <!-- woocommerce_show_product_images -->
                <?php
                /**
                 * woocommerce_show_product_images hook
                 *
                 * @hooked woocommerce_show_product_sale_flash - 10
                 * @hooked woocommerce_show_product_images - 20
                 */
                do_action('woocommerce_before_single_product_summary');
                ?>
            </div>
        </div>
        <div class="col-lg-8 col-md-8 col-sm-12 col-xs-12 clear_xs">
            <div class="content_product_detail">
                <!-- woocommerce_template_single_title - 5 -->
                <!-- woocommerce_template_single_rating - 10 -->
                <!-- woocommerce_template_single_price - 20 -->
                <!-- woocommerce_template_single_excerpt - 30 -->
                <!-- woocommerce_template_single_add_to_cart 40 -->
                <?php
                /**
                 * woocommerce_single_product_summary hook
                 *
                 * @hooked woocommerce_template_single_title - 5
                 * @hooked woocommerce_template_single_price - 10
                 * @hooked woocommerce_template_single_excerpt - 20
                 * @hooked woocommerce_template_single_add_to_cart - 30
                 * @hooked woocommerce_template_single_meta - 40
                 * @hooked woocommerce_template_single_sharing - 50
                 */
                do_action('woocommerce_single_product_summary');
                ?>
            </div><!-- close col 8 tag in rating -->
            <div class="col-md-4">
                <?php
                $terms = get_the_terms($post->ID, "product_brand");
                if (!empty(get_post_meta(get_the_ID(), 'orario', true)) || !empty($terms)) {
                    echo '<h3>ویژگی‌های محصول</h3><ul class="cus-style">';
                    if (is_array($terms) || is_object($terms)) {
                        echo '<li>برند : ';
                        foreach ($terms as $term) {
                            echo '<a href="' . get_term_link($term->term_id, 'product_brand') . '"> ' . $term->name . ' </a>';
                        }
                        echo '</li>';
                    }
                    if (!empty(get_post_meta(get_the_ID(), 'orario', true))) {
                        echo '<li>' . get_post_meta(get_the_ID(), 'orario', true) . '</li>';
                    }
                    if (!empty(get_post_meta(get_the_ID(), 'luogo_evento', true))) {
                        echo '<li>' . get_post_meta(get_the_ID(), 'luogo_evento', true) . '</li>';
                    }
                    if (!empty(get_post_meta(get_the_ID(), 'indirizzo', true))) {
                        echo '<li>' . get_post_meta(get_the_ID(), 'indirizzo', true) . '</li>';
                    }
                    if (!empty(get_post_meta(get_the_ID(), 'zona', true))) {
                        echo '<li>' . get_post_meta(get_the_ID(), 'zona', true) . '</li>';
                    }
                    if (!empty(get_post_meta(get_the_ID(), 'contatti', true))) {
                        echo '<li>' . get_post_meta(get_the_ID(), 'contatti', true) . '</li>';
                    }
                    echo '</ul>';
                }
                ?>

            </div>
        </div><!-- close row tag in rating -->
    </div>
    <?php
    if (class_exists('Dokan_Vendor')) {
        $seller = get_post_field('post_author', $product->get_id());
        $author = get_user_by('id', $seller);
        $store_info = dokan_get_store_info($author->ID);
        $dps_processing = get_user_meta($post->post_author, '_dps_pt', true);
        $processing_time = $dps_processing;
        echo '<div class="zanbil-vendor"><div class="dc"><p><img src="data:image/svg+xml;utf8;base64," />';
        printf('فروشنده: <a href="%s">%s</a>', dokan_get_store_url($author->ID), $author->display_name);
        if (!empty($store_info['store_name'])) {
            echo '<br> نام فروشگاه : ' . $store_info['store_name'] . '</span>';
        }
        echo '</p></div>';

        if ($processing_time) { ?>
            <div class="dc"><p><img
                        src="data:image/svg+xml;utf8;base64,"/><?php _e('Ready to ship in', 'dokan'); ?>
                    <br><?php echo dokan_get_processing_time_value($processing_time); ?></p></div>
        <?php }
        echo '<div class="dc"><img src="data:image/svg+xml;utf8;base64," /><a style="color: #109bb7;" href="' . get_permalink(get_page_by_title('فروشنده شوید')) . '" class="register" title="فروشنده شوید"><span>فروشنده شوید و محصولات خود را در سایت ما به فروش برسانید.</span></a></div></div>';
    }


    $short_description = apply_filters('woocommerce_short_description', $post->post_excerpt);
    if ($short_description) {

        echo '<div class="woocommerce-product-details__short-description">' . $short_description . '</div>'; // WPCS: XSS ok.

    }
    ?>
</div>
</div>
</div>
<div class="tabs clearfix">
    <?php
    /**
     * woocommerce_after_single_product_summary hook
     *
     * @hooked woocommerce_output_product_data_tabs - 10
     * @hooked woocommerce_output_related_products - 20
     */
    do_action('woocommerce_after_single_product_summary');
    ?>
</div>

